    @Override
    public int[] getItemIds() {
        return ITEM_IDS;
    }

    @Override
    protected boolean useItemImpl(Player player, ItemInstance item, boolean ctrl) {
        int itemId = item.getItemId();

        if (SkillTable.getInstance().identifySkills.get(itemId) != null && !player.isCastingNow()) {
            player.doCast(SkillTable.getInstance().getInfo(SkillTable.getInstance().identifySkills.get(itemId), 1), player, false);
            return true;
        } else
            return false;
    }
